import React from "react";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({
  contacts,
  name,
  title,
  setTitle,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {

  return (
    <>
    <form onSubmit={handleSubmit} >
      <label>
        <input 
          type="text"
          name="name"
          value={name}
          onChange={(e)}
        />
      </label>
      <br />

      <label>

      </label>
      <br />
      <label>
        <input 
          type="date"
          name="date"
          value={date}
          />
      </label>
      <br />

      <label>
        <input 
          type="time"
          name="appointment time"
          value={time}
        />
      </label>
      <br />
    </form>
    </>
  );
};
