    <div>
      <section>
        <h2>Add Contact</h2> 
          <ContactForm 
            name={name}

          />
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
          <TileList 
            items={props.contacts}
          />
      </section>
    </div>
  );
};
